targets.expr:
sum by (mp_name, mp_gps, mp_ip, task_name) (
    apatit_mp_data_staleness_steps{} * $refresh_interval_seconds / 60
    ) >= $max_actual_minutes

sort(
    (sum by (mp_name, task_name) (
        apatit_mp_data_staleness_steps{}
        ) >= 3)
    /
    (sum by (mp_name, task_name) (
        apatit_mp_data_staleness_steps{}
        ) >= 3)
)

sum by (mp_name, mp_gps, mp_ip) (
    apatit_mp_data_staleness_steps{
        task_name="$task_name", 
        mp_name=~"$location_name"
        }
    ) * $refresh_interval_seconds / 60

sum by (mp_name, mp_gps, mp_ip) (
  apatit_mp_total_duration_seconds{
    task_name="$task_name",
    mp_name=~"$location_name"
  }
)
